pub enum InvalidTransaction {

    /// The call of the transaction is not expected.
    Call,

    /// General error to do with the inability to pay some fees (e.g. account balance too low).
    Payment,

    /// General error to do with the transaction not yet being valid (e.g. nonce too high).
    Future,

    /// General error to do with the transaction being outdated (e.g. nonce too low).
    Stale,

    /// General error to do with the transaction's proofs (e.g. signature).
    ///
    /// # Possible causes
    ///
    /// When using a signed extension that provides additional data for signing, it is required
    /// that the signing and the verifying side use the same additional data. Additional
    /// data will only be used to generate the signature, but will not be part of the transaction
    /// itself. As the verifying side does not know which additional data was used while signing
    /// it will only be able to assume a bad signature and cannot express a more meaningful error.
    BadProof,

    /// The transaction birth block is ancient.
    ///
    /// # Possible causes
    ///
    /// For `FRAME`-based runtimes this would be caused by `current block number
    /// - Era::birth block number > BlockHashCount`. (e.g. in Polkadot `BlockHashCount` = 2400, so a
    /// transaction with birth block number 1337 would be valid up until block number 1337 + 2400,
    /// after which point the transaction would be considered to have an ancient birth block.)
    AncientBirthBlock,

    /// The transaction would exhaust the resources of current block.
    ///
    /// The transaction might be valid, but there are not enough resources
    /// left in the current block.
    ExhaustsResources,

    /// Any other custom invalid validity that is not covered by this enum.
    Custom(u8),

    /// An extrinsic with a Mandatory dispatch resulted in Error. This is indicative of either a
    /// malicious validator or a buggy `provide_inherent`. In any case, it can result in dangerously
    /// overweight blocks and therefore if found, invalidates the block.
    BadMandatory,

    /// A transaction with a mandatory dispatch. This is invalid; only inherent extrinsics are
    /// allowed to have mandatory dispatches.
    MandatoryDispatch,
}

pub enum UnknownTransaction {

    /// Could not lookup some information that is required to validate the transaction.
    CannotLookup,

    /// No validator found for the given unsigned transaction.
    NoUnsignedValidator,

    /// Any other custom unknown validity that is not covered by this enum.
    Custom(u8),
}

pub enum TransactionValidityError {

    /// The transaction is invalid.
    Invalid(InvalidTransaction),

    /// Transaction validity can't be determined.
    Unknown(UnknownTransaction),
}

pub enum TransactionSource {

    /// Transaction is already included in block.
    ///
    /// This means that we can't really tell where the transaction is coming from,
    /// since it's already in the received block. Note that the custom validation logic
    /// using either `Local` or `External` should most likely just allow `InBlock`
    /// transactions as well.
    InBlock,

    /// Transaction is coming from a local source.
    ///
    /// This means that the transaction was produced internally by the node
    /// (for instance an Off-Chain Worker, or an Off-Chain Call), as opposed
    /// to being received over the network.
    Local,

    /// Transaction has been received externally.
    ///
    /// This means the transaction has been received from (usually) "untrusted" source,
    /// for instance received over the network or RPC.
    External,
}

pub enum RuntimeString {

    /// The borrowed mode that wraps a `&'static str`.
    Borrowed(&'static str),

    /// The owned mode that wraps a `String`.
    #[cfg(feature = "std")]
    Owned(String),
}

pub enum MultiAddress<AccountId, AccountIndex> {

    /// It's an account ID (pubkey).
    Id(AccountId),

    /// It's an account index.
    Index(
          #[codec(compact)]
          AccountIndex),

    /// It's some arbitrary raw bytes.
    Raw(Vec<u8>),

    /// It's a 32 byte representation.
    Address32([u8; ]),

    /// Its a 20 byte representation.
    Address20([u8; ]),
}

pub enum MultiSignature {

    /// An Ed25519 signature.
    Ed25519(ed25519::Signature),

    /// An Sr25519 signature.
    Sr25519(sr25519::Signature),

    /// An ECDSA/SECP256k1 signature.
    Ecdsa(ecdsa::Signature),
}

pub enum MultiSigner {

    /// An Ed25519 identity.
    Ed25519(ed25519::Public),

    /// An Sr25519 identity.
    Sr25519(sr25519::Public),

    /// An SECP256k1/ECDSA identity (actually, the Blake2 hash of the compressed pub key).
    Ecdsa(ecdsa::Public),
}

pub enum DispatchError {

    /// Some error occurred.
    Other(
          #[codec(skip)]
          #[serde(skip_deserializing)]
          &'static str),

    /// Failed to lookup some data.
    CannotLookup,

    /// A bad origin.
    BadOrigin,

    /// A custom error in a module.
    Module {
        /// Module index, matching the metadata module index.
        index: u8,
        /// Module specific error value.
        error: u8,
        /// Optional error message.
        #[codec(skip)]
        #[serde(skip_deserializing)]
        message: Option<&'static str>,
    },

    /// At least one consumer is remaining so the account cannot be destroyed.
    ConsumerRemaining,

    /// There are no providers so the account cannot be created.
    NoProviders,
}

pub enum TransactionOutcome<R> {

    /// Commit the transaction.
    Commit(R),

    /// Rollback the transaction.
    Rollback(R),
}
